function myRouter() {
  // hash watcher
  window.onhashchange = function () {
    setTimeout(function () {
      checkHash();
    }, 100);
  };
  /*_________funcs_____*/


  function checkHash() {
    var location = window.location.hash;
    var main = document.querySelector('.mainContent');
    var name = location.substring(2, location.length);
    if (name == '') name = 'base';
    check_obj(name);

    for (var item in pages) {
      //clear_classes
      main.classList.remove(pages[item]["class"]);
    }

    for (var _item in pages) {
      var page = document.querySelector(".".concat(_item));

      if (pages[_item].value) {
        main.classList.add(pages[_item]["class"]);
        page.classList.add('_activePage');
      } else {
        page.classList.remove('_activePage');
      }
    }

    function check_obj(page) {
      for (var _item2 in pages) {
        if (_item2 == page) {
          pages[_item2].value = true;
        } else {
          pages[_item2].value = false;
        }
      }
    }
  }

  checkHash();
}

